import React,{Component} from 'react'
import {data} from './data'

const ProductContext=React.createContext();


class  ProductProvider extends Component {
    
    constructor(){
        super()
        this.state = {
            data: data,
            chapter:{},
            articleContent:[]
            
        }
    }
    
    OpenArticle=(chapter)=>{
    
        const chapterIndex=this.state.data.find(o=>o.title===chapter)
        let contentPreload=[]
        console.log(chapterIndex)
        chapterIndex.body.map((value,index)=>{
            const {isImage,imageSrc,content} = value
            console.log(imageSrc)
            return contentPreload.push(isImage?<img className='article-img' src={require(`{imageSrc}`)} key={index} alt='image'/>:<p key={index}>{content}</p>)
        })

        this.setState(()=>{
            return {
                chapter: chapterIndex,
                articleContent:contentPreload}
        })         

        
    }
    
    render(){
        return(
            <ProductContext.Provider value ={{
            
            OpenArticle:this.OpenArticle,
            articleContent:this.state.articleContent,

            }}>
                {this.props.children}
            </ProductContext.Provider>
        )
    }
}

const ProductConsumer= ProductContext.Consumer;

export {ProductProvider, ProductConsumer}
