import React,{Component} from 'react'
import {data} from './data'

const ProductContext=React.createContext();


class  ProductProvider extends Component {
    
    constructor(){
        super()
        this.state = {
            data: data,
            chapter:{},
            articleContent:[],
            test:1
            
        }
    }
    
    OpenArticle=(chapter)=>{
    
        const chapterIndex=this.state.data.find(o=>o.title===chapter)
        let contentPreload=[]
        console.log(chapterIndex)
        chapterIndex.body.map(value=>{
            return contentPreload.push(<p>value.content</p>)
        })
        this.setState(()=>{
            return {articleContent:contentPreload}
        })
            console.log(this.state.articleContent)


        
    }
    
    render(){
        return(
            <ProductContext.Provider value ={{
            
            OpenArticle:this.OpenArticle,
            articleContent:this.state.articleContent,

            }}>
                {this.props.children}
            </ProductContext.Provider>
        )
    }
}

const ProductConsumer= ProductContext.Consumer;

export {ProductProvider, ProductConsumer}
