    super(props);
    this.state = {
      
    };
    }
    
    componentDidMount(){
        if (this.props.match.params.id)
            alert("empty")
    }
    
    
    render(){
        return(
        <ProductConsumer>
            
            
                    {value=>{
                      
                        return(
                            <div className="article-container">
                                <div className="article-body">                           
                                    {value.articleContent}
                                </div>
                            </div>
                        )
                    }}
        </ProductConsumer>
        )
    }
}


export default article
